var NeedIt_UpdateIfs = Class.create();
NeedIt_UpdateIfs.prototype = {
    initialize: function() {
		var target = new GlideRecord('needit') ; 
		target.addQuery('u_score','<',10);
		target.query(); // Issue the query to the database to get relevant records 
		while (target.next()) { 
			alert("u_range = " + 'u_range');
			target.setValue('u_range', "Less than 10");
			target.update();
}

    }

    //type: 'NeedIt_UpdateIfs'
};
